import{_ as t,c as a,o as d,a3 as r}from"./chunks/framework.DXSyntku.js";const e="/tio2data/assets/1718290493453.CLN4TNyV.png",o="/tio2data/assets/1718290533856.BsHK89Qx.png",x=JSON.parse('{"title":"钛白粉生产工艺","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge/生产工艺/index.md","filePath":"knowledge/生产工艺/index.md"}'),n={name:"knowledge/生产工艺/index.md"},s=r('<h1 id="钛白粉生产工艺" tabindex="-1">钛白粉生产工艺 <a class="header-anchor" href="#钛白粉生产工艺" aria-label="Permalink to &quot;钛白粉生产工艺&quot;">​</a></h1><p>钛白粉的制造主要有两种工艺流程：硫酸法和氯化法。理论上，氯化法在产品质量上更胜一筹，但此工艺对技术和设备的要求极高。</p><h2 id="硫酸法钛白粉生产工艺" tabindex="-1">硫酸法钛白粉生产工艺 <a class="header-anchor" href="#硫酸法钛白粉生产工艺" aria-label="Permalink to &quot;硫酸法钛白粉生产工艺&quot;">​</a></h2><p><img src="'+e+'" alt="alt text"> ** 龙佰 ** 年报截图</p><h2 id="氯化法生产工艺" tabindex="-1">氯化法生产工艺 <a class="header-anchor" href="#氯化法生产工艺" aria-label="Permalink to &quot;氯化法生产工艺&quot;">​</a></h2><p><img src="'+o+'" alt="alt text"></p><p>** 龙佰 ** 年报截图</p><h2 id="生产工艺对比-硫酸法与氯化法" tabindex="-1">生产工艺对比：硫酸法与氯化法 <a class="header-anchor" href="#生产工艺对比-硫酸法与氯化法" aria-label="Permalink to &quot;生产工艺对比：硫酸法与氯化法&quot;">​</a></h2><h2 id="氯化法与硫酸法比较" tabindex="-1">氯化法与硫酸法比较 <a class="header-anchor" href="#氯化法与硫酸法比较" aria-label="Permalink to &quot;氯化法与硫酸法比较&quot;">​</a></h2><table tabindex="0"><thead><tr><th>项目</th><th>硫酸法</th><th>氯化法</th></tr></thead><tbody><tr><td><strong>原料</strong></td><td>- 钛铁矿：价廉、供应稳定，可直接开采<br>- 酸溶性钛渣：品质较好但价格较高，需经化学处理</td><td>- 钛铁矿/白钛石：工艺要求高，价格较低<br>- 金红石：工艺要求不高，价格较高<br>- 氯化渣、人造金红石：工艺要求不高，价格更高</td></tr><tr><td><strong>产品类型</strong></td><td>锐钛型钛白粉、金红石型钛白粉</td><td>仅生产金红石型钛白粉</td></tr><tr><td><strong>生产技术</strong></td><td>- 应用历史悠久，资料齐全，易掌握<br>- 水解和煅烧环节需精密控制以确保最佳粒径</td><td>- 技术较新，氯化氧化工艺含有较多技术秘密<br>- 少数公司公开TiCl4氧化技术</td></tr><tr><td><strong>产品质量</strong></td><td>- 可比肩氯化法产品，通过工艺优化和包膜技术减小差距</td><td>- 纯度高、质量好，但含有微量氯和HCl，具腐蚀性，应用受限</td></tr><tr><td><strong>其他原材料</strong></td><td>- 硫酸：来源多样（如烟气、黄铁矿），成本随硫磺价格波动</td><td>- 氯气：成本随能耗和烧碱生产需求变化，多数可循环利用，对低成本原料厂影响小</td></tr><tr><td><strong>污染与废物处理</strong></td><td>- 废酸产生量大（每吨钛白粉约8吨20%浓度废酸），已有回收处理技术</td><td>- 废物排放少，但处理责任重大；低品位原料可能导致大量含氯废物（每吨钛白粉高达1.6吨FeCl3），处理方式环保性差</td></tr><tr><td><strong>工厂安全</strong></td><td>- 主要风险来自热浓硫酸处理，液体存储相对简单</td><td>- 风险源于氯气和高温TiCl4气体泄漏，对环境威胁大，储存条件苛刻</td></tr></tbody></table><p>尽管硫酸法在环保及产业升级方面面临挑战，但从实用性和技术普及度来看，并非处于绝对劣势。国家虽倾向于鼓励氯化法生产以促进环保和产业升级，但由于国内企业缺乏氯化法的核心技术，氯化法的实际推广仍面临重重困难。</p>',11),i=[s];function l(h,c,_,p,g,b){return d(),a("div",null,i)}const u=t(n,[["render",l]]);export{x as __pageData,u as default};
